let canvas,ctx,width=300,height=300,angle=0,dA=1;function init(){height=window.innerHeight,width=window.innerWidth,canvas=document.getElementById("sync"),document.getElementById("speed").addEventListener("change",(t=>{dA=parseFloat(t.target.value)*Math.PI})),canvas.width=width,canvas.height=height,canvas.getContext&&(ctx=canvas.getContext("2d"),setInterval(loop,50))}function scroll(t){}function loop(){angle+=dA,ctx.fillStyle="black",ctx.fillRect(0,0,width,height),ctx.save(),ctx.translate(width/2,height/2),ctx.beginPath(),ctx.rotate(angle),ctx.lineWidth=4,ctx.strokeStyle="white",ctx.arc(0,0,300,0,.2),ctx.stroke(),ctx.beginPath(),ctx.strokeStyle="red",ctx.arc(0,0,300,Math.PI,Math.PI+.2),ctx.stroke(),ctx.restore()}init();